mapper.select:

select sum(cnt) from (select count(*) as cnt from plan_auth_log as l right outer join
official_plan_log as p on l.plan_id = p.official_plan_log_id where p.member_email = #{email}
union all
select count(*) as cnt from plan_auth_log as l right outer join
non_official_plan as n on l.plan_id = n.non_official_plan_id where n.member_email = #{email}) as tmp;
